import React, { useState, useEffect } from 'react';
import Button from './components/ui/button';
import { AlertCircle } from 'lucide-react';

const NumberRoulette = () => {
  const [isSpinning, setIsSpinning] = useState(false);
  const [currentNumber, setCurrentNumber] = useState(1);
  const [finalNumber, setFinalNumber] = useState(null);

  useEffect(() => {
    let interval;
    if (isSpinning) {
      interval = setInterval(() => {
        setCurrentNumber((prevNumber) => (prevNumber % 10) + 1);
      }, 100);
    } else if (finalNumber) {
      clearInterval(interval);
      setCurrentNumber(finalNumber);
    }
    return () => clearInterval(interval);
  }, [isSpinning, finalNumber]);

  const handleSpin = () => {
    setIsSpinning(true);
    setFinalNumber(null);
    playSound('/spin-start.mp3');
    setTimeout(() => {
      setIsSpinning(false);
      const newFinalNumber = Math.floor(Math.random() * 10) + 1;
      setFinalNumber(newFinalNumber);
      playSound('/spin-stop.mp3');
    }, 3000);
  };

  const playSound = (soundFile) => {
    const audio = new Audio(soundFile);
    audio.play();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-purple-400 via-pink-500 to-red-500">
      <div className={`text-8xl font-bold mb-8 transition-all duration-300 ${isSpinning ? 'animate-bounce text-yellow-300' : finalNumber ? 'animate-pulse text-green-300' : 'text-white'}`}>
        {currentNumber}
      </div>
      <Button
        onClick={handleSpin}
        disabled={isSpinning}
        className={`px-6 py-3 text-xl font-semibold rounded-full shadow-lg transition-all duration-300 ${
          isSpinning ? 'bg-gray-500 cursor-not-allowed' : 'bg-blue-500 hover:bg-blue-600 active:bg-blue-700'
        }`}
      >
        {isSpinning ? (
          <AlertCircle className="w-6 h-6 animate-spin" />
        ) : (
          'Tourner'
        )}
      </Button>
    </div>
  );
};

export default NumberRoulette;
